namespace App\Util;

class PasswordGenerator
{
    private $words;

    public static function password() : string
    {
        return (new static)->generate();
    }

    public function generate() : string
    {
        $source = $this->getWords();
        $seed = [$this->randomWord(3, 5), $this->randomWord(4, 6)];
        shuffle($seed);
        list($a, $b) = $seed;
        $c = rand(11, 99);

        return implode('', [$a, $c, $b]);
    }

    private function randomWord(int $minLength, int $maxLength) : string
    {
        $words = $this->getWords();
        $a = rand($minLength, $maxLength);
        $b = rand(0, count($words[$a]) - 1);
        return $words[$a][$b];
    }

